// Let's draw a rectangle

rect(100, 150, 200, 150);
//   ^    ^     ^     ^
//   x    y   width  height

// Now let's add some color
fill(255, 100, 50);
//   ^     ^    ^
//   red  green blue

// These rectangles now appear as orange
rect(50, 50, 50, 50);
rect(150, 50, 50, 50);
rect(250, 50, 50, 50);
rect(350, 50, 50, 50);
rect(450, 50, 50, 50); // increasing the x-coordinate
                       // means going more to the right!


// What other shapes can we draw?
fill(50, 150, 255);

// "ellipse" is basically a circle, but can have different width/height
ellipse(300, 500, 50, 50);
//     (x, y, width, height) just like rect

// This is a squished one
//                 \/ see the increased "width" parameter
ellipse(300, 565, 150, 50);

// Let's make some variables!

// These are red
fill(250, 50, 100);
rect(100, 100, 50, 25);
rect(100, 150, 50, 25);
rect(100, 200, 50, 25);
rect(100, 250, 50, 25);
rect(100, 300, 50, 25);
//    ^ hmm notice a lot of repeated numbers?

// \/ now we've made a variable called "x"
var x = 200;

// Anytime we use "x" now, it means 200

// These are green
fill(50, 250, 100);
rect(x, 100, 50, 25);
rect(x, 150, 50, 25);
rect(x, 200, 50, 25);
rect(x, 250, 50, 25);
rect(x, 300, 50, 25);
//   ^ all of these have an x-coordinate of 200

// We can also reassign variables
x = 300;

// From this point on, x will be 300
// Let's run the exact same commands as before
// and let's make them blue
fill(50, 100, 250);
rect(x, 100, 50, 25);
rect(x, 150, 50, 25);
rect(x, 200, 50, 25);
rect(x, 250, 50, 25);
rect(x, 300, 50, 25);
// See, the same command as before produced a different result
// because the value of "x" was changed!
